
public class TesteBoolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int idadeMaiorIdade= 18;
		int idade = 19;
		boolean resultado;
		
		resultado = idade >= idadeMaiorIdade;
		if (resultado == true) {
			System.out.println("Você é maior de idade.");
		}
		else {
			System.out.println("Você é menot de idade.");
		}
	}

}
